import React, { Component } from 'react';
import MiComponente from './MiComponente';
import Peliculas from './Peliculas';


class SeccionPruebas extends Component {

    //var HolaMundo = ()=> {}
    contador = 0;

    HolaMundo(nombre, edad) {
        var presentacion = (
            <div>
                <h2>Hola Soy {nombre}</h2>
                <h2>Tengo {edad} años</h2>
            </div>
        );
        return presentacion;
    }

    sumar() {
        this.contador++
    }
    restar() {
        this.contador--
    }

    render() {
        var nombre = 'Pedro Moreno'

        return (
            <section id="content">
                <h2 className="subheader">Últimos artículos</h2>
                <p>Hola desde el curso de React</p>
                <h2 className="subheader">Funciones y JSX basico</h2>
                {this.HolaMundo(nombre, 36)}
                <h2 className="subheader">Componentes</h2>
                <section className="componentes">
                    <MiComponente />
                    <Peliculas />
                </section>
                <h2 className="subheader">Estado</h2>
                <p>Contador: {this.contador}</p>
                <p>
                    <input type="button" value="Sumar" onClick={sumar} />
                    <input type="button" value="Restar" onClick={restar} />
                </p>
            </section>
        );
    }
}

export default SeccionPruebas;
