import React, { Component } from 'react';
import axios from 'axios';
import Global from '../Global';
import Moment from 'react-moment';
import 'moment/locale/es';
import { Link } from 'react-router-dom';

class Articles extends Component {

    url = Global.url;

    state = {
        articles: [],
        status: null
    }

    componentWillMount() {
        var home = this.props.home;
        var search = this.props.search;

        if (home === 'true') {
            this.getLastArticles();
        } else if (search && search !== null && search !== undefined) {
            this.getArticlesBySearch(search);
        } else {
            this.getArticles();
        }

    }

    getArticlesBySearch = (searched) => {
        axios.get(this.url + "search/" + searched).then(res => {
            this.setState({
                articles: res.data.articles,
                status: "success"
            });
        }).catch(err => {
            this.setState({
                articles: [],
                status: "success"
            });
        })
    }

    getLastArticles = () => {
        axios.get(this.url + "articles/last").then(res => {
            console.log(res.data);
            this.setState({
                articles: res.data.articles,
                status: "success"
            })

        });
    }

    getArticles = () => {
        axios.get(this.url + "articles").then(res => {
            console.log(res.data);
            this.setState({
                articles: res.data.articles,
                status: "success"
            })
            console.log(this.state)
        });
    }

    render() {
        if (this.state.articles.length >= 1) {

            var listArticles = this.state.articles.map((article) => {
                return (
                    <React.Fragment key={article._id}>
                        <article className="article-item" id="article-template">
                            <div className="image-wrap">
                                {article.image != null ? (
                                    <img src={this.image + "get-image" + article.image} alt={article.title} />
                                ) : (
                                        <img src="https://unhabitatmejor.leroymerlin.es/sites/default/files/styles/header_category/public/2018-10/4%20paisaje%20macedonia.jpg?itok=AELknmF8" alt={article.title} />
                                    )

                                }

                            </div>

                            <h2>{article.title}</h2>
                            <span className="date">
                                <Moment fromNow>{article.date}</Moment>
                            </span>
                            <Link to={"/blog/articulo/" + article._id}>Leer más</Link>

                            <div className="clearfix"></div>
                        </article>
                    </React.Fragment>

                )
            })

            return (
                <div id="articles">
                    <h1>{listArticles}</h1>
                </div>
            );

        } else if (this.state.length === 0 && this.state.status === 'success') {
            return (
                <div id="articles">
                    <h2 className="subheader">Todavia no hay contenido en esta sección</h2>
                </div>
            );
        } else {
            return (
                <div id="articles">
                    <h2 className="subheader">Cargando...</h2>
                </div>
            );

        }

    }
}

export default Articles;
