import React, { Component } from 'react';
import axios from 'axios';

class Articles extends Component {

    state = {
        articles: [],
        status: null
    }

    componentWillMount() {
        this.getArticles();
    }

    getArticles = () => {
        axios.get("http://localhost:3900/api/articles").then(res => {
            console.log(res.data);
            this.setState({
                articles: res.data.articles,
                status: "success"
            })
            console.log(this.state)
        });
    }

    render() {
        if(this.state.articles.length >=1){
            return (
                <div id="articles">
                    <h1>Hola</h1>
                </div>
            );

        }else if(this.state.length === 0 && this.state.status === 'success'){
            return (
                <div id="articles">
                    <h2 className="subheader">Todavia no hay contenido en esta secci√≥n</h2>
                </div>
            );
        }else{
            return (
                <div id="articles">
                    <h2 className="subheader">Cargando...</h2>
                </div>
            );

        }
        
    }
}

export default Articles;
